class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bigChartData: "data1",
      testdata: null,
      numberOfAlerts: null,
      alerts: [],
      signalStrength: null,
      deviceStatus: null,
      lat: 0,
      long: 0
    };
  }
  setBgChartData = name => {
    this.setState({
      bigChartData: name
    });
  };
  // ,{'mode': 'no-cors'}
  componentDidMount() {
    this.timer = setInterval(() => this.getAlerts(), 1000);
    this.timer = setInterval(() => this.getSignal(), 1000);
    this.timer = setInterval(() => this.getDeviceStatus(), 1000);
    this.timer = setInterval(() => this.getLocation(), 10000);
  }
  componentWillReceiveProps(nextProps) {
    this.setState({
      lat: nextProps.lat,
      long: nextProps.long
    });
  }

  async getAlerts() {
    fetch("http://127.0.0.1:5000/error", { method: "GET" })
      .then(response => response.json())
      .then(responseData => {
        //set your data here
        let numberOfAlerts = responseData.length;
        this.setState({
          numberOfAlerts: numberOfAlerts,
          alerts: responseData
        });
      })
      .catch(error => {
        console.error(error);
      });
  }

  async getSignal() {
    fetch("http://127.0.0.1:5000/sigstrength", { method: "GET" })
      .then(response => response.json())
      .then(responseData => {
        //set your data here
        this.setState({
          signalStrength: responseData[0]["SIGNAL STRENGTH"]
        });
      })
      .catch(error => {
        console.error(error);
      });
  }

  async getDeviceStatus() {
    fetch("http://127.0.0.1:5000/checkdev", { method: "GET" })
      .then(response => response.json())
      .then(responseData => {
        //set your data here
        this.setState({
          deviceStatus: responseData
        });
      })
      .catch(error => {
        console.error(error);
      });
  }
  async getLocation() {
    fetch("http://127.0.0.1:5000//latlong", { method: "GET" })
      .then(response => response.json())
      .then(responseData => {
        //set your data here
        this.setState({
          lat: parseFloat(responseData[0]["LONGITUDE"]),
          long: parseFloat(responseData[0]["LATTITUDE"])
        });
        this.forceUpdate();
        console.log(parseFloat(responseData[0]["LATTITUDE"]));
      })
      .catch(error => {
