                      </tr>
                    </thead>
                    <tbody className="tablebody">
                      {this.state.alerts.map(item =>
                        //  console.log(item["DATE"] + item["TIME"])
                        <tr>
                          <td>{item["DATE"] + " " + item["TIME"]}</td>
                          <td>Temperature Sensor Not Working</td>
                          <td className="text-center">{item["ERROR CODE"]}</td>
                        </tr>
                      )}
