import React from "react";
import "./Dashboard.css";
//import Map from "../components/Map";
// nodejs library that concatenates classes
import classNames from "classnames";
// react plugin used to create charts
import { Line } from "react-chartjs-2";
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';

// reactstrap components
import {
  Button,
  ButtonGroup,
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Table,
  Row,
  Col
} from "reactstrap";

// core components
// import { chartExample1 } from "../variables/charts.jsx";
// import LineGraph from "../variables/linegraph";

let datemax  = new Date("April 1, 2019 21:13:00");
let datemin = new Date(datemax.getDate() - 13);



let chart1_2_options = {
  maintainAspectRatio: false,
  legend: {
    display: false
  },


  elements: {
    line: {
    tension: 0
    }
    },

  responsive: true,
  scales: {
    yAxes: [
      {
        gridLines: {
          drawBorder: false,
          color: "rgba(29,140,248,0.0)",
          zeroLineColor: "transparent"
        },
        ticks: {
          suggestedMin: 0,
          suggestedMax: 100,
          padding: 20,

          fontColor: "#9a9a9a"
        }
      }
    ],
    xAxes: [
      {
        type: 'time',
        // distribution: 'linear',

        time: {
            unit : 'day',
            displayFormats: {
                day: 'MMM DD',
            },
            // max: datemax,
            // min: datemin,
        },
        barPercentage: 1.6,
        gridLines: {
          drawBorder: false,
          color: "rgba(29,140,248,0.1)",
          zeroLineColor: "transparent"
        },
        ticks: {
          fontColor: "#9a9a9a",
          // source: 'data',
        }
      }
    ]
  }
};



class Dashboard extends React.Component {
  constructor(props) {
    super(props);

    this.getAlerts = this.getAlerts.bind(this);
    this.getDeviceStatus = this.getDeviceStatus.bind(this);
    this.getTemperature = this.getTemperature.bind(this);
    this.getSignal = this.getSignal.bind(this);
    this.getLocation = this.getLocation.bind(this);
    this.state = {
      bigChartData: "data1",
      testdata: null,
      numberOfAlerts: null,
      alerts: [],
      signalStrength: null,
      deviceStatus: null,
      lat: 0,
      long: 0,
      temperature: [],
      chartExample1 : {
        data1: canvas => {
          let ctx = canvas.getContext("2d");

          let gradientStroke = ctx.createLinearGradient(0, 230, 0, 50);

          gradientStroke.addColorStop(1, "rgba(29,140,248,0.2)");
          gradientStroke.addColorStop(0.4, "rgba(29,140,248,0.0)");
          gradientStroke.addColorStop(0, "rgba(29,140,248,0)"); //blue colors
          // let td = moment('2019-04-01')
          let date = []
          date[0]  = new Date();
           date[1] = new Date(date[0].getDate() - 1);
          date[2] = new Date(date[0].getDate() - 2);

           date[3] = new Date(date[0].getDate() - 3);
           date[4] = new Date(date[0].getDate() - 4);
           date[5] = new Date(date[0].getDate() - 5);
           date[6] = new Date(date[0].getDate() - 6);
           date[7] = new Date(date[0].getDate() - 7);
           date[8] = new Date(date[0].getDate() - 8);
           date[9] = new Date(date[0].getDate() - 9);
           let dateObj = new Date();


          return {
          //   plugins: [{
          //     beforeInit: function(chart) {
          //        var time = chart.options.scales.xAxes[0].time, // 'time' object reference
          //           // difference (in days) between min and max date
          //           timeDiff = moment(time.max).diff(moment(time.min), 'd');
          //        // populate 'labels' array
          //        // (create a date string for each date between min and max, inclusive)
          //        for (i = 0; i <= timeDiff; i++) {
          //           var _label = moment(time.min).add(i, 'd').format('YYYY-MM-DD HH:mm:ss');
          //           chart.data.labels.push(_label);
          //        }
          //     }
          //  }],

            //  labels : [new Date("March 31, 2019"), new Date("April 1, 2019"),new Date("April 2, 2019"),new Date("April 3, 2019"),new Date("April 4, 2019")],
            // labels : ["1","2","3","4"],
            labels : [ new Date(),

                      dateObj.setDate(dateObj.getDate() - 1) ,
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                    ],
            // labels : [date[0] ,
            //   date[1],
            //   date[2],
            // ],
            datasets: [
              {
                label: "Temperature",
                fill: true,
                backgroundColor: gradientStroke,
                borderColor: "#1f8ef1",
                borderWidth: 2,
                borderDash: [],
                borderDashOffset: 0.0,
                pointBackgroundColor: "#1f8ef1",
                pointBorderColor: "rgba(255,255,255,0)",
                pointHoverBackgroundColor: "#1f8ef1",
                pointBorderWidth: 0,
                pointHoverRadius: 0,
                pointHoverBorderWidth: 0,
                pointRadius: 0,
                data: this.state.temperature
                // data : [40,3,2,1]
              }
            ]
          };
        },

        data2: canvas => {
          let ctx = canvas.getContext("2d");

          let gradientStroke = ctx.createLinearGradient(0, 230, 0, 50);

          gradientStroke.addColorStop(1, "rgba(29,140,248,0.2)");
          gradientStroke.addColorStop(0.4, "rgba(29,140,248,0.0)");
          gradientStroke.addColorStop(0, "rgba(29,140,248,0)"); //blue colors
          // let td = moment('2019-04-01')
          let date = []
          date[0]  = new Date();
           date[1] = new Date(date[0].getDate() - 1);
          date[2] = new Date(date[0].getDate() - 2);

           date[3] = new Date(date[0].getDate() - 3);
           date[4] = new Date(date[0].getDate() - 4);
           date[5] = new Date(date[0].getDate() - 5);
           date[6] = new Date(date[0].getDate() - 6);
           date[7] = new Date(date[0].getDate() - 7);
           date[8] = new Date(date[0].getDate() - 8);
           date[9] = new Date(date[0].getDate() - 9);
           let dateObj = new Date();


          return {
          //   plugins: [{
          //     beforeInit: function(chart) {
          //        var time = chart.options.scales.xAxes[0].time, // 'time' object reference
          //           // difference (in days) between min and max date
          //           timeDiff = moment(time.max).diff(moment(time.min), 'd');
          //        // populate 'labels' array
          //        // (create a date string for each date between min and max, inclusive)
          //        for (i = 0; i <= timeDiff; i++) {
          //           var _label = moment(time.min).add(i, 'd').format('YYYY-MM-DD HH:mm:ss');
          //           chart.data.labels.push(_label);
          //        }
          //     }
          //  }],

            //  labels : [new Date("March 31, 2019"), new Date("April 1, 2019"),new Date("April 2, 2019"),new Date("April 3, 2019"),new Date("April 4, 2019")],
            // labels : ["1","2","3","4"],
            labels : [ new Date(),

                      dateObj.setDate(dateObj.getDate() - 1) ,
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                      dateObj.setDate(dateObj.getDate() - 1),
                    ],
            // labels : [date[0] ,
            //   date[1],
            //   date[2],
            // ],
            datasets: [
              {
                label: "Temperature",
                fill: true,
                backgroundColor: gradientStroke,
                borderColor: "#1f8ef1",
                borderWidth: 2,
                borderDash: [],
                borderDashOffset: 0.0,
                pointBackgroundColor: "#1f8ef1",
                pointBorderColor: "rgba(255,255,255,0)",
                pointHoverBackgroundColor: "#1f8ef1",
                pointBorderWidth: 0,
                pointHoverRadius: 0,
                pointHoverBorderWidth: 0,
                pointRadius: 0,
                data: this.state.temperature
                // data : [40,3,2,1]
              }
            ]
          };
        },

        options: chart1_2_options
      },
    };
  }


  setBgChartData = name => {
    this.setState({
      bigChartData: name
    });
  };
  // ,{'mode': 'no-cors'}

  componentDidMount() {
    this.interval = setInterval(() => this.getTemperature(), 1000);
    this.interval = setInterval(() => this.getAlerts(), 1000);
    this.interval = setInterval(() => this.getSignal(), 1000);
    this.interval = setInterval(() => this.getDeviceStatus(), 1000);
    this.interval = setInterval(() => this.getLocation(), 5000);
  }
  componentWillReceiveProps(nextProps) {
    this.setState({
      lat: nextProps.lat,
      long: nextProps.long
    });
  }

  async getTemperature() {
    fetch("http://127.0.0.1:5000/temperature", { method: "GET" })
      .then(response => response.json())
      .then(responseData => {
        //set your data here


        let a = [];
        let i = 0;
        // newdate = date.split("/").reverse().join("-");
        for (let index = 0; index < responseData.length - 1; index++) {
          let x = responseData[index]["DATE"];
          let newdate = x
            .split("/")
            .reverse()
            .join("-");
          let t1 = newdate + " " + responseData[index]["TIME"];
          let y = responseData[index + 1]["DATE"];
          let newdate1 = y
            .split("/")
            .reverse()
            .join("-");
          let t2 = newdate1 + " " + responseData[index + 1]["TIME"];
          let td = new Date(t2) - new Date(t1);
          // let td2 = new Date("April 1, 2019 21:13:00") - new Date(t1)
          let td2 = new Date() - new Date(t1)

          if (td > 380000 && td2<1209600000) {
            a[i] = {};
            a[i]["x"] = newdate + " " + responseData[index]["TIME"];
            a[i]["y"] = responseData[index]["TEMPERATURE"];
            i = i + 1;
            a[i] = {};
            a[i]["x"] = newdate + " " + responseData[index]["TIME"];
            a[i]["y"] = 0;
            i = i + 1;
            a[i] = {};
            a[i]["x"] = newdate1 + " " + responseData[index + 1]["TIME"];
            a[i]["y"] = 0;
            i = i + 1;
            a[i] = {};
            a[i]["x"] = newdate1 + " " + responseData[index + 1]["TIME"];
            a[i]["y"] = responseData[index + 1]["TEMPERATURE"];
              i = i + 1;

          } else if(td2<1209600000)  {
            a[i] = {};
            a[i]["x"] = newdate + " " + responseData[index]["TIME"];
            a[i]["y"] = responseData[index]["TEMPERATURE"];
            i = i + 1;
            a[i] = {};
            a[i]["x"] = newdate1 + " " + responseData[index + 1]["TIME"];
            a[i]["y"] = responseData[index + 1]["TEMPERATURE"];
              i = i + 1;

          }
        }



        this.setState({
          temperature: a
        });
        //console.log(this.state.temperature);
      })
      .catch(error => {
        console.error(error);
      });
  }

  async getAlerts() {
    fetch("http://127.0.0.1:5000/error", { method: "GET" })
      .then(response => response.json())
      .then(responseData => {
        //set your data here
        let numberOfAlerts = responseData.length;
        this.setState({
          numberOfAlerts: numberOfAlerts,
          alerts: responseData
        });
      })
      .catch(error => {
        console.error(error);
      });
  }

  async getSignal() {
    fetch("http://127.0.0.1:5000/sigstrength", { method: "GET" })
      .then(response => response.json())
      .then(responseData => {
        //set your data here
        this.setState({
          signalStrength: responseData[0]["SIGNAL STRENGTH"]
        });
      })
      .catch(error => {
        console.error(error);
      });
  }

  async getDeviceStatus() {
    fetch("http://127.0.0.1:5000/checkdev", { method: "GET" })
      .then(response => response.json())
      .then(responseData => {
        //set your data here
        let deviceDate = responseData[0]["DATE"];
        deviceDate = deviceDate
          .split("/")
          .reverse()
          .join("-");
        deviceDate = deviceDate+ " " + responseData[0]["TIME"];
